import React, { useState } from 'react';
import { Input, Button, Space } from 'antd';
import { askAI } from '../api';

const { TextArea } = Input;

const MessageInput = ({ chatHistory, setChatHistory, parameters, setLoading }) => {
    const [inputValue, setInputValue] = useState("");

    const handleSend = async () => {
        if (!inputValue.trim()) return;
        // 将用户消息追加到历史中
        const newHistory = [...chatHistory, { role: 'user', content: inputValue }];
        setChatHistory(newHistory);
        setInputValue("");
        setLoading(true);
        try {
            // 调用后端接口，同时传递参数和上下文（如需要）
            const response = await askAI(newHistory, parameters);
            // 假设 API 返回结果在 response.answer 中
            setChatHistory([...newHistory, { role: 'assistant', content: response.answer }]);
        } catch (error) {
            // 出错时追加错误消息
            setChatHistory([...newHistory, { role: 'assistant', content: "请求出错，请重试。" }]);
        }
        setLoading(false);
    };

    return (
        <Space style={{ marginTop: '10px', width: '100%' }} direction="vertical">
            <TextArea
                rows={3}
                value={inputValue}
                onChange={(e) => setInputValue(e.target.value)}
                placeholder="输入消息..."
            />
            <Button type="primary" onClick={handleSend}>发送</Button>
        </Space>
    );
};

export default MessageInput;
