import React, { useState } from 'react';
import { Input, Button, Card } from 'antd';
import { askAI } from './api';

function App() {
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');

  const handleAsk = async () => {
    if (!question.trim()) return;
    const response = await askAI(question);
    setAnswer(response.answer || '未返回答案');
  };

  return (
      <div style={{ padding: 20, maxWidth: 600, margin: 'auto' }}>
        <h2>PPIO AI Chat</h2>
        <Input.TextArea rows={3} value={question} onChange={e => setQuestion(e.target.value)} placeholder="输入你的问题" />
        <Button type="primary" onClick={handleAsk} style={{ marginTop: 10 }}>提交</Button>
        {answer && <Card style={{ marginTop: 20 }}>{answer}</Card>}
      </div>
  );
}

export default App;
