import React, { useEffect, useRef } from 'react';
import { List, Avatar, Spin } from 'antd';

const ChatWindow = ({ chatHistory, loading }) => {
    const endRef = useRef(null);

    useEffect(() => {
        endRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [chatHistory, loading]);

    return (
        <div style={{ height: '60vh', overflowY: 'auto', padding: '10px', background: '#fff', borderRadius: '5px', border: '1px solid #f0f0f0' }}>
            <List
                dataSource={chatHistory}
                renderItem={(item) => (
                    <List.Item style={{ textAlign: item.role === 'user' ? 'right' : 'left', padding: '8px' }}>
                        <List.Item.Meta
                            avatar={<Avatar>{item.role === 'user' ? 'U' : 'AI'}</Avatar>}
                            title={item.role === 'user' ? '你' : 'AI'}
                            description={item.content}
                            style={{ backgroundColor: item.role === 'user' ? '#f0f5ff' : '#f7f7f7', borderRadius: '8px', padding: '10px' }}
                        />
                    </List.Item>
                )}
            />
            {loading && (
                <div style={{ textAlign: 'center', padding: '10px' }}>
                    <Spin tip="AI 正在思考..." />
                </div>
            )}
            <div ref={endRef} />
        </div>
    );
};

export default ChatWindow;
