import React, { useEffect, useRef } from 'react';
import { List, Avatar, Spin } from 'antd';

const ChatWindow = ({ chatHistory, loading }) => {
    const endRef = useRef(null);

    useEffect(() => {
        endRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [chatHistory, loading]);

    return (
        <div style={{ height: '60vh', overflowY: 'auto', padding: '10px', background: '#fff', borderRadius: '10px', border: '1px solid #f0f0f0' }}>
            <List
                dataSource={chatHistory}
                renderItem={(item) => (
                    <List.Item style={{ display: 'flex', flexDirection: item.role === 'user' ? 'row-reverse' : 'row', marginBottom: '10px' }}>
                        <List.Item.Meta
                            avatar={<Avatar style={{ backgroundColor: item.role === 'user' ? '#1890ff' : '#87d068', color: '#fff' }}>{item.role === 'user' ? 'U' : 'AI'}</Avatar>}
                            title={
                                <span style={{ fontWeight: 'bold', color: item.role === 'user' ? '#1890ff' : '#333' }}>
                                    {item.role === 'user' ? '你' : 'AI'}
                                </span>
                            }
                            description={
                                <div style={{
                                    backgroundColor: item.role === 'user' ? '#e6f7ff' : '#f0f0f0',
                                    padding: '10px',
                                    borderRadius: '10px',
                                    maxWidth: '80%',
                                    wordBreak: 'break-word',
                                    boxShadow: '0 2px 5px rgba(0, 0, 0, 0.1)',
                                    marginLeft: item.role === 'user' ? '10px' : '0',
                                    marginRight: item.role === 'user' ? '0' : '10px',
                                    fontSize: '16px'
                                }}>
                                    {item.content}
                                </div>
                            }
                        />
                    </List.Item>
                )}
            />
            {loading && (
                <div style={{ textAlign: 'center', padding: '10px' }}>
                    <Spin tip="AI 正在思考..." />
                </div>
            )}
            <div ref={endRef} />
        </div>
    );
};

export default ChatWindow;
