import React, { useEffect, useRef } from 'react';
import { motion } from 'framer-motion';

const ChatWindow = ({ chatHistory = [], loading = false }) => {
    const endRef = useRef(null);

    useEffect(() => {
        endRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [chatHistory, loading]);

    return (
        <div className="h-[60vh] overflow-y-auto p-4 bg-white rounded-lg">
            <div className="space-y-4">
                {chatHistory.map((item, index) => (
                    <div
                        key={index}
                        className={`flex ${item.role === 'user' ? 'flex-row-reverse' : 'flex-row'} items-start gap-2`}
                    >
                        <div
                            className={`flex-shrink-0 w-8 h-8 rounded-full flex items-center justify-center text-white
                                ${item.role === 'user' ? 'bg-green-500' : 'bg-blue-500'}`}
                        >
                            {item.role === 'user' ? 'U' : 'AI'}
                        </div>

                        <div className={`flex flex-col ${item.role === 'user' ? 'items-end' : 'items-start'}`}>
                            <div className="flex items-center gap-2">
                                <span>{item.role === 'user' ? '你' : 'AI'}</span>
                                <span className="text-xs text-gray-500">
                                    {new Date(item.timestamp).toLocaleTimeString()}
                                </span>
                            </div>

                            <motion.div
                                initial={{ opacity: 0, x: item.role === 'user' ? 50 : -50 }}
                                animate={{ opacity: 1, x: 0 }}
                                transition={{ type: "spring", stiffness: 100 }}
                                className={`mt-1 px-4 py-2 rounded-2xl max-w-[80%] text-white
                                    ${item.role === 'user' ? 'bg-green-500' : 'bg-blue-500'}`}
                            >
                                {item.content}
                            </motion.div>
                        </div>
                    </div>
                ))}
            </div>

            {loading && (
                <div className="text-center py-4">
                    <div className="inline-block animate-spin rounded-full h-8 w-8 border-4 border-gray-300 border-t-blue-500" />
                    <div className="mt-2 text-gray-600">AI 正在思考...</div>
                </div>
            )}
            <div ref={endRef} />
        </div>
    );
};

export default ChatWindow;
